begin
wwv_flow_imp_shared.create_list(
 p_id=>wwv_flow_imp.id(58640400250682392147)
,p_name=>'SYSTEM_MENU'
,p_list_type=>'SQL_QUERY'
,p_list_query=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select level,',
'       MENU_CAPTION label, ',
'       decode(page_ID, null, null, ''f?p=&APP_ID.:''||"PAGE_ID"||'':&APP_SESSION.'') target, ',
'       case ',
'            when instr('',''',
'              || PAGE_LIST',
'              || '','', '','' || :APP_PAGE_ID || '','') > 0 then',
'            ''YES''',
'            else',
'              ''NO''',
'       end as is_current,',
'       ICON',
'   from ',
'  (',
'  SELECT MENU_ID,MENU_NAME MENU_CAPTION,MENU_LABEL,PAGE_ID,PAGE_LIST,ICON,PARENT_MENU_ID,IS_ACTIVE FROM APP_MENUS  ',
'   )  T',
'   WHERE 1=1   ',
'   AND EXISTS (select RM.MENU_ID',
' from  USER_ROLES R,APP_USERS U,ROLE_MENUS RM',
' WHERE R.ROLE_ID=U.USER_ROLE_ID',
' AND R.ROLE_ID=RM.ROLE_ID',
' AND UPPER(U.USER_ID)=UPPER(:APP_USER) AND T.MENU_ID=RM.MENU_ID)',
'  start with PARENT_MENU_ID is null',
'connect by prior MENU_ID  = PARENT_MENU_ID',
'order siblings by MENU_ID ',
''))
,p_list_status=>'PUBLIC'
,p_version_scn=>15637615209005
);
