'DECLARE',
'vId Number;',
'Begin',
'select nvl(max(DELIVERY_ID),0)+1 into vId from DELIVERY_MASTER;',
'insert into DELIVERY_MASTER(DELIVERY_ID,VICTIM_ID,VOLUNTEER_ID)',
'values(vId,to_number(:P34_VICTIM_ID),to_number(:P34_VOLUNTEER));',
'',
'',
'    DECLARE',
'    CURSOR C1 IS ',
'    SELECT ITEM_ID,',
'           UOM_ID,	',
'           INV_QTY,',
'           PRV_DEL_QTY,',
'           NVL(INV_QTY,0)-NVL(PRV_DEL_QTY,0)DEL_QTY ',
'           FROM(',
'            select i.ITEM_ID,i.UOM_ID,I.QUANTITY INV_QTY,',
'            NVL((SELECT Sum(D.QTY) FROM DELIVERY_MASTER M, DELIVERY_DETAILS D WHERE M.DELIVERY_ID=D.DELIVERY_ID AND M.VICTIM_ID=to_number(:P34_VICTIM_ID) AND D.ITEM_ID=i.ITEM_ID),0)PRV_DEL_QTY',
'            from VICTIM_NEEDS_ITEMS i',
'            where VICTIM_ID	=to_number(:P34_VICTIM_ID))',
'            where NVL(INV_QTY,0)-NVL(PRV_DEL_QTY,0)>0;',
'',
'',
'        BEGIN',
'',
'            FOR REC_C1 IN C1 LOOP',
'            INSERT INTO DELIVERY_DETAILS(',
'                        DELIVERY_ID,ITEM_ID,UOM_ID,INV_QTY,PRV_DEL_QTY,QTY,REMARKS)',
'                        VALUES(vId,REC_C1.ITEM_ID,REC_C1.UOM_ID,REC_C1.INV_QTY,REC_C1.PRV_DEL_QTY,REC_C1.DEL_QTY,NULL);',
'            END LOOP;',
'    END;',
'',
'',
'end;'))
,p_attribute_02=>'P34_VICTIM_ID,P34_VOLUNTEER'
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
