wwv_flow_imp_page.create_page_da_action(
 p_id=>wwv_flow_imp.id(61501506326710186641)
,p_event_id=>wwv_flow_imp.id(61501505935322186637)
,p_event_result=>'TRUE'
,p_action_sequence=>20
,p_execute_on_page_init=>'N'
,p_action=>'NATIVE_EXECUTE_PLSQL_CODE'
,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    vId            NUMBER;',
'    vVictimID      NUMBER;',
'    vVolunteerID   NUMBER;',
'BEGIN',
'    -- Validate and safely convert inputs',
'    IF :P34_VICTIM_ID IS NULL OR NOT REGEXP_LIKE(:P34_VICTIM_ID, ''^\d+$'') THEN',
'        RAISE_APPLICATION_ERROR(-20001, ''Invalid Victim ID'');',
'    END IF;',
'',
'    IF :P34_VOLUNTEER IS NULL OR NOT REGEXP_LIKE(:P34_VOLUNTEER, ''^\d+$'') THEN',
'        RAISE_APPLICATION_ERROR(-20002, ''Invalid Volunteer ID'');',
'    END IF;',
'',
'    -- Assign to variables',
'    vVictimID := TO_NUMBER(:P34_VICTIM_ID);',
'    vVolunteerID := TO_NUMBER(:P34_VOLUNTEER);',
'',
'    -- Get new DELIVERY_ID',
'    SELECT NVL(MAX(DELIVERY_ID), 0) + 1 INTO vId',
'    FROM DELIVERY_MASTER;',
'',
'    -- Insert into DELIVERY_MASTER',
'    INSERT INTO DELIVERY_MASTER (DELIVERY_ID, VICTIM_ID, VOLUNTEER_ID)',
'    VALUES (vId, vVictimID, vVolunteerID);',
'',
'    -- Insert into DELIVERY_DETAILS using a cursor',
'    DECLARE',
'        CURSOR C1 IS',
'            SELECT ITEM_ID,',
'                   UOM_ID,',
'                   INV_QTY,',
'                   PRV_DEL_QTY,',
'                   NVL(INV_QTY, 0) - NVL(PRV_DEL_QTY, 0) AS DEL_QTY',
'              FROM (',
'                    SELECT i.ITEM_ID,',
'                           i.UOM_ID,',
'                           i.QUANTITY AS INV_QTY,',
'                           NVL((',
'                               SELECT SUM(d.QTY)',
'                               FROM DELIVERY_MASTER m',
'                               JOIN DELIVERY_DETAILS d ON m.DELIVERY_ID = d.DELIVERY_ID',
'                               WHERE m.VICTIM_ID = vVictimID',
'                                 AND d.ITEM_ID = i.ITEM_ID',
'                           ), 0) AS PRV_DEL_QTY',
'                      FROM VICTIM_NEEDS_ITEMS i',
'                     WHERE i.VICTIM_ID = vVictimID',
'                   )',
'             WHERE NVL(INV_QTY, 0) - NVL(PRV_DEL_QTY, 0) > 0;',
'',
'    BEGIN',
'        FOR REC_C1 IN C1 LOOP',
'            INSERT INTO DELIVERY_DETAILS (',
'                DELIVERY_ID,',
'                ITEM_ID,',
'                UOM_ID,',
'                INV_QTY,',
'                PRV_DEL_QTY,',
'                QTY,',
'                REMARKS',
'            )',
'            VALUES (',
'                vId,',
'                REC_C1.ITEM_ID,',
'                REC_C1.UOM_ID,',
'                REC_C1.INV_QTY,',
'                REC_C1.PRV_DEL_QTY,',
'                REC_C1.DEL_QTY,',
'                NULL',
'            );',
'        END LOOP;',
'    END;',
'',
'END;',
''))
,p_attribute_02=>'P34_VICTIM_ID,P34_VOLUNTEER'
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
);
